import { useState } from 'react';
import { X } from 'lucide-react';

const App = () => {
      const [title, setTitle] = useState('')
      const [details, setDetails] = useState('')
  
      const submitHandler = (e) =>{
      e.preventDefault();
      
      const copyTask = [...task];
      copyTask.push({title, details})
  
      setTask(copyTask);
  
      setTitle('');
      setDetails('');
    }

  const [task, setTask] = useState([])

 


  const deleteNote= (idx) => {
     const copyTask = [...task];
     copyTask.splice(idx,1);
     setTask(copyTask);
  }

    // two way binding
  return (<>  
    
    <div className='bg-black min-h-screen lg:flex lg:overflow-hidden  text-white'>
      <form 
      onSubmit={(e)=>{
        submitHandler(e)
      }}
      className='flex flex-col  lg:w-1/2 p-10  items-start  gap-4' >
        <h1 className="text-4xl font-bold">Add Notes</h1>
        {/* pehla input or heading  */}
        <input 
        type="text" 
        placeholder='Enter Notes Heading'  
        className='border-2 w-full  rounded px-4 py-3 text-xl mt-5 m-2'
        value={title}
        onChange={(e)=>{
          setTitle(e.target.value);
          
        }}
        />

        {/* detailed input */}
        <textarea 
        type="text" 
        placeholder='Write Details'  
        className='border-2 rounded px-4 py-3 m-2 w-full outline-none h-50 '
        value={details}
        onChange={(e)=>{
          setDetails(e.target.value)
           
        }}
        />
        <button 
        className='bg-white text-black rounded p-2 m-2 text-2xl font-bold  active:scale-95 active:bg-blue-300'
        >
          Add Notes
        </button>
      </form>

      <div className='lg:w-1/2 p-10 lg:border-l-2  '>
      <h1 className="text-4xl font-bold">Recent Notes</h1>
      <div className="flex flex-wrap overflow-auto gap-5 h-full mt-5">
          {task.map(function(elem, idx){
            return <div 
             
             key={idx} 
            className=" relative rounded-2xl h-65  group relative: bg-cover text-black p-4 w-55  bg-[url(data:image/jpeg;base64,)] ">
              <button
                onClick={() => deleteNote(idx)}
                className="
                  absolute top-4 right-4
                  p-2 rounded-full
                  bg-white/10 backdrop-blur
                  text-black/70

                  opacity-0
                  translate-x-2 -translate-y-2

                  transition-all duration-300
                  group-hover:opacity-100
                  group-hover:translate-x-0
                  group-hover:translate-y-0

                  hover:text-red-800
                  active:scale-90
                "
              >
                <X size={18} />
              </button>


              <h3 className='leading-tight mt-2 p-2  overflow-x-hidden text-xl font-bold'>{elem.title}</h3>
              <p className='m-2  leading-tight font-medium wrap-break-word  text-gray-700 ' > {elem.details}</p>
            </div>
          })}

      </div>
      </div>
    </div>
    </>
  )
}

export default App